import  React,{ Component } from "react";

const debounce = (func, wait) => {
  let timeout
  return (...args) => {
    clearTimeout(timeout)
    timeout = setTimeout(() => func.apply(this, args), wait)
  }
}

const styleHeader = {
    backgroundColor: '#fff',
    padding: '10px',
    textDecoration: 'none',
    position: 'fixed',
    width: '100%',
    zIndex: '2',
    top: '0',
    height: '30px',
    boxShadow: '0px 1px 7px 0px rgba(0, 0, 0, 0.4)',
    display: 'flex',
    transition: 'top 0.2s ease-in-out',
};

 class Navbar extends Component {
  constructor(props) {
    super(props)
    this.state = {
      styleHeader: styleHeader,
    }
  }

  componentDidMount() {
     window.addEventListener('scroll', this.handleScroll.bind(this));
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll.bind(this));
  }

  handleScroll (evt){
    let previous = this.state.scrollY;
   let current = evt.path[0].scrollY;
   let delta = current - previous;

   // Mutating styles directly is deprecated as of v0.14, so make a copy
   let localStyleHeader = JSON.parse(JSON.stringify(this.state.styleHeader));

   if (delta < 0) {
       // scrolled up
       //
       localStyleHeader.top = '0px';
   } else if (delta > 0) {
       // scrolling down
       //
       if (scrollY >= 30) {
           // use the styleHeader.height value to maintain
           // the padding above the top elements
           localStyleHeader.top = '-50px';
       }
   }

   this.setState({
       styleHeader: localStyleHeader,
       scrollY: current,
   });
  }

  render() {
    let localStyleHeader = this.state.styleHeader;
    return (
      <div style={localStyleHeader}>
        <header>
          <div className="container clearfix">
            <h1 id="logo">Apples</h1>
            <nav>
              <a href="#bacon">Bacon</a>
              <a href="#sausage">Sausage</a>
              <a href="#biltong">Biltong</a>
            </nav>
          </div>
        </header>
      </div>
    );
  }
}

export default Navbar;
